//#region IMPORTS
import React, { useState, useEffect } from 'react';
import { Link, useHistory } from "react-router-dom";
import { FiPower, FiTrash2 } from "react-icons/fi";
// import api from "../../services/api";
import "./Home.css";
//#endregion

export default function Perfil() {

    //#region CONST
    const [casos, setCasos] = useState([]);
    const history = useHistory();
    const ongId = localStorage.getItem('ongId');
    const ongNome = localStorage.getItem('ongNome');
    //#endregion

    //#region GET
    useEffect(() => {
        // api.get('perfil', {
        //     headers: {
        //         Authorization: ongId,
        //     }
        // }).then(response => {
        //     setCasos(response.data)
        // })
    }, [ongId]);
    //#endregion

    //#region DELETE
    async function deletarCaso(id) {
        // try {
        //     await api.delete(`casos/${id}`, {
        //         headers: {
        //             Authorization: ongId,
        //         }
        //     });
        //     setCasos(casos.filter(caso => caso.id !== id));

        // } catch (error) {
        //     alert('Erro ao deletar caso, tente novamente');
        // }
    }
    //#endregion

    //#region LOGOUT
    function Logout() {
        localStorage.clear();
        history.push('/');
    }
    //#endregion

    //#region HTML
    return (
        <div className="perfil-container">
            <header>
                {/* <img src={logoImg} alt="Be the hero"></img> */}
                <span>Bem vindo, {ongNome}</span>

                <Link className="button" to="/casos/novo">Cadastrar novo caso</Link>
                <button onClick={Logout} type="button">
                    <FiPower size={18} color="#E02041" />
                </button>
            </header>

            <h1>Casos cadastrados</h1>

            <ul>
                {casos.map(caso => (
                    <li key={caso.id}>
                        <strong>CASO:</strong>
                        <p>{caso.titulo}</p>

                        <strong>DESCRIÇÃO:</strong>
                        <p>{caso.descricao}</p>

                        <strong>VALOR:</strong>
                        <p>{Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(caso.valor)}</p>

                        <button onClick={() => deletarCaso(caso.id)} type="button">
                            <FiTrash2 size={20} color="#a8a8b3" />
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
    //#endregion
}
